import './App.css';
import React from "react";
import { Country } from "country-state-city";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      currentCountry: null,
      countries: Country.getAllCountries(),
    };
    this.handleCountryChange = this.handleCountryChange.bind(this);
  }

  handleCountryChange(event) {
    const selectedCountry = this.state.countries.find(
      (country) => country.name === event.target.value
    );
    this.setState({ currentCountry: selectedCountry });
    console.log(selectedCountry.name);
    var req = new XMLHttpRequest();
    req.open('GET', '/search/' + selectedCountry.name, true);
    req.onreadystatechange = function(){
      if (req.readyState === 4) {
        console.log(req.responseText);
      }
    };
    req.send();
  }

  render() {
    const countryOptions = this.state.countries.map((country) => (
      <option key={country.isoCode} value={country.name}>
        {country.name}
      </option>
    ));

    return (
      <div className="App">
        <div className="App-input-block">
          <h1 className="input-header">Test</h1>
          <div className="input">
            <select
              value={this.state.currentCountry ? this.state.currentCountry.name : ""}
              onChange={this.handleCountryChange}
            >
              <option value="">Select a country</option>
              {countryOptions}
            </select>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
